import React, { useState, useRef, useEffect } from "react";
import "../assets/styles/NavBar.css";
import { RegisterBtn, SignInBtn } from "../components";
import { StylesProvider } from "../context/NavBarStyleContext";
import styles from "../assets/styles/RegisterSignIn.module.css";
import { GoQuestion } from "react-icons/go";
import { FaSearchLocation } from "react-icons/fa";

const NavBar = () => {
  const [focus, setFocus] = useState<boolean>(false);
  const [searchString, setSearchString] = useState<string>("");
  const [mouseOver, setMouseOver] = useState<boolean>(false);
  const inputRef = useRef<HTMLInputElement>(null!);

  const arr: string[] = ["Bomso", "Ayeduase", "Kotei", "New Site", "New City"];

  const handleFocus = () => {
    setFocus(true);
    (inputRef.current as HTMLElement).focus();
  };
  const handleBlur = () => {
    if (mouseOver) {
      setFocus(true);
    } else {
      setFocus(false);
    }
  };

  const areaDisplay = () => {
    arr.map(
      (el, index) =>
        el.toLocaleLowerCase().includes(searchString) && (
          <li
            key={index}
            className="area"
            style={
              index % 2 === 0
                ? { backgroundColor: "#222f36", color: "#d9f1e1" }
                : { backgroundColor: "#d9f1e1", color: "#222f36" }
            }
            onMouseOver={() => {
              setMouseOver(true);
              // console.log("mouse over");,
              console.log(`Mouse Over Container ${focus}`);
            }}
            onClick={({ target }) => {
              if (mouseOver) {
                console.log(`Mouse Click 1st Container ${focus}`);
                // console.log("Mouse Over Clicked");
                setFocus(true);
                console.log(`Mouse Click 2nd Container ${focus}`);
                handleFocus();

                const searchStr = (target as HTMLElement).innerHTML;
                (inputRef.current as HTMLInputElement).value = searchStr;
                // console.log(focus);
                // setFocus(false);
              } else {
                setFocus(false);
                console.log(`Mouse Click Else Block Container ${focus}`);

                // console.log("Mouse Over Clicked outside");
              }
            }}
          >
            {el}
          </li>
        )
    );
  };

  // useEffect(() => {}, []);
  console.log("SEARCH STRING: ", searchString);
  return (
    <>
      <div className="nav">
        <a href="/" className="urlTitle">
          Hostelbook.com
        </a>
        <div className="hostelFilterByAreaNameContainer">
          <FaSearchLocation
            style={{
              fontSize: "2rem",
              color: "#222f36",
            }}
          />
          <input
            ref={inputRef}
            className="hostelFilterByAreaName"
            type="text"
            placeholder="Filter hostel by area name"
            onFocus={handleFocus}
            onBlur={handleBlur}
            onChange={(e) => {
              setSearchString(e.target.value);
            }}
          />
        </div>
        <div
          className="availableAreasContainer"
          style={focus ? { display: "block" } : { display: "none" }}
          onMouseLeave={() => {
            setMouseOver(false);
            handleBlur();

            console.log(`Mouse Left Container ${focus}`);
          }}
        >
          <ul className="availableAreas">
            {/* {arr.map(
              (el, index) =>
                el.toLocaleLowerCase().includes(searchString) && (
                  <li
                    key={index}
                    className="area"
                    style={
                      index % 2 === 0
                        ? { backgroundColor: "#222f36", color: "#d9f1e1" }
                        : { backgroundColor: "#d9f1e1", color: "#222f36" }
                    }
                    onMouseOver={() => {
                      setMouseOver(true);
                      // console.log("mouse over");,
                      console.log(`Mouse Over Container ${focus}`);
                    }}
                    onClick={({ target }) => {
                      if (mouseOver) {
                        console.log(`Mouse Click 1st Container ${focus}`);
                        // console.log("Mouse Over Clicked");
                        setFocus(true);
                        console.log(`Mouse Click 2nd Container ${focus}`);
                        handleFocus();

                        const searchStr = (target as HTMLElement).innerHTML;
                        (inputRef.current as HTMLInputElement).value =
                          searchStr;
                        // console.log(focus);
                        // setFocus(false);
                      } else {
                        setFocus(false);
                        console.log(
                          `Mouse Click Else Block Container ${focus}`
                        );

                        // console.log("Mouse Over Clicked outside");
                      }
                    }}
                  >
                    {el}
                  </li>
                )
            )} */}

            {/* {set && areaDisplay()} */}
          </ul>
        </div>

        <StylesProvider styles={styles}>
          <div className={styles.registerSignInBtnWrapper}>
            <GoQuestion
              className={styles.customerService}
              style={{ color: "#222f36", fontSize: "1.8rem" }}
              title="Contact customer service"
            />
            <a className={styles.listProperty} href="/">
              List your property
            </a>
            <RegisterBtn />
            <SignInBtn />
          </div>
        </StylesProvider>
      </div>
    </>
  );
};

export default NavBar;
