import React, { useState, useRef, useEffect } from "react";
import "./DisplayHostels.css";
import hostelTwo from "../../assets/images/hostel-2.webp";
import { RiHeartAdd2Line } from "react-icons/ri";
import { IoMdStar } from "react-icons/io";

// type stars =

// type MyArray =
//   | { name: "Alice", age: 15 }
//   | { name: "Bob", age: 23 }
//   | { name: "Eve", age: 38 }

// interface hostelDetailsProp {
//   name: string;
//   stars: number;
//   street: string;
//   distance: number;
//   remark: string;
//   reviews: number;
//   rate: number;
//   locationRate?: number
// }

const DisplayHostels = () => {
  const [isSave, setIsSave] = useState<boolean>(false);
  const iconRef = useRef<HTMLButtonElement>(null);

  const star = (count: number) =>
    Array.from({ length: count }, (_, i) => (
      <IoMdStar key={i} size={25} color="orange" />
    ));

  useEffect(() => {
    (iconRef.current as HTMLButtonElement).addEventListener("click", (e) => {
      e.preventDefault();
      setIsSave(!isSave);

      console.log(e.target);
    });
  }, [isSave]);

  return (
    <>
      <div className="displayHostelContainer">
        <div className="hostelInfoWrapper">
          <div className="imageContainer">
            <a href="/" className="imageBody">
              <img
                src={hostelTwo}
                alt="hostel pic"
                height={250}
                width={230}
                className="hostelImage"
              />
              <button
                type="button"
                className="save-btn"
                title="save"
                ref={iconRef}
              >
                <RiHeartAdd2Line
                  size={40}
                  color="black"
                  style={{
                    position: "absolute",
                    top: "5%",
                    left: "75%",
                    backgroundColor: isSave ? "#eb34e1" : "#ffff",
                    padding: "5px",
                    borderRadius: "50%",
                    zIndex: "1000",
                  }}
                />
              </button>
            </a>
          </div>
          <div className="hostelInfo">
            <div className="hostelInfo-info">
              <p>
                {`the prince hostel`}
                {star(5)}
              </p>
              <p>
                <a href="/">{`Ayeduase gate road block 8884`}</a>
                <a href="/">Show on map</a>
                <span className="distance">{2} km from KNUST campus</span>
              </p>
              <p>
                {`Nestled amidst the serene hills, our hostel is a tranquil
                retreat for travelers seeking solace and adventure. Located just
                a stone's throw away from the bustling city center, our hostel
                offers the perfect blend of convenience and escapism. Surrounded
                by lush greenery and panoramic views, guests can unwind in our
                cozy accommodations after a day of exploring nearby attractions.
                `}
              </p>
            </div>
            <div className="hostelInfo-review">
              <div className="hostelInfo-review--rate">
                <div>
                  <p style={{ fontWeight: "bold" }}> {"Very good"} </p>
                  <pre>{12345} reviews</pre>
                </div>
                <p
                  style={{
                    backgroundColor: "blue",
                    width: "20%",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    borderRadius: "10px 10px 10px 0px",
                    color: "white",
                  }}
                >
                  {4.5}
                </p>
              </div>
              <a className="location-rate" href="/">
                Location {9.7}
              </a>
              <button type="button" className="show-price--btn">
                Show price
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default DisplayHostels;
