import React from "react";
import "./DisplayHostels.css";
import hostelTwo from "../../assets/images/hostel-2.webp";
import { RiHeartAdd2Line } from "react-icons/ri";
import { MdOutlineStarBorder } from "react-icons/md";

const DisplayHostels = () => {
  return (
    <>
      <div className="displayHostelContainer">
        <p>Total: 30 hostels found</p>
        <div className="sortedByContainer">
          <span>Sorted by:</span>
          <select>
            <option value="top-picks">Our top picks</option>
            <option value="hostel-apartment">Hostels & apartment first</option>
            <option value="rating-high">Property rating (high to low)</option>
            <option value="rating-low">Property rating (low to high)</option>
            <option value="distance">Distance from KNUST campus</option>
            <option value="reviewed">Top reviewed</option>
          </select>
        </div>
        <div className="hostelInfoWrapper">
          <div className="imageContainer">
            <a href="/" className="imageBody">
              <img
                src={hostelTwo}
                alt="hostel pic"
                height={250}
                width={230}
                className="hostelImage"
              />
              <RiHeartAdd2Line
                size={40}
                color="black"
                style={{
                  position: "absolute",
                  top: "5%",
                  left: "75%",
                  backgroundColor: "#eb34e1",
                  padding: "5px",
                  borderRadius: "50%",
                }}
              />
            </a>
          </div>
          <div className="hostelInfo">
            <div className="hostelInfo-info">
              <p>
                the prince hostel <sup>{<MdOutlineStarBorder size={25} />}</sup>
              </p>
            </div>
            <div className="hostelInfo-review"></div>
          </div>
        </div>
      </div>
    </>
  );
};

export default DisplayHostels;
